mapper.select:
SELECT r.*, m.menuName FROM recipe r JOIN menu m ON r.mcode
= m.mcode WHERE
r.rcode = #{rcode}

SELECT DISTINCT r.*, m.menuName FROM recipe r JOIN menu m ON r.mcode = m.mcode WHERE 1=1
AND r.category IN
#{cat}
AND r.rcode NOT IN ( SELECT DISTINCT ri.rcode FROM recipe_ingredient ri WHERE ri.icode IN
#{ing}
)
AND (
r.theme LIKE CONCAT('%', #{thm}, '%')
)
AND (r.recipeName LIKE CONCAT('%', #{searchTerm}, '%') OR r.introduce LIKE CONCAT('%', #{searchTerm}, '%') OR m.menuName LIKE CONCAT('%', #{searchTerm}, '%'))
